// Extract topic and payload
let topicParts = msg.topic.split("/");  // Split topic into an array
let value = msg.payload;  // Get the new value

// Ensure topic structure is correct (at least 6 parts for workspaces)
if (topicParts.length < 5) {
    node.warn("Invalid topic structure: " + msg.topic);
    return null;
}

// Extract manufacturer, serial number, section, and workspace
let manufacturer = topicParts[1];  // "default_manufacturer"
let serial = topicParts[2];  // "default_serial_number"

// Retrieve or initialize stored data (persistent across messages)
let fleetData = flow.get("fleetData") || {};

// Ensure manufacturer exists
if (!fleetData[manufacturer]) {
    fleetData[manufacturer] = {};
}

// Ensure serial number exists
if (!fleetData[manufacturer][serial]) {
    fleetData[manufacturer][serial] = {
        manufacturer: manufacturer,
        serial: serial,
        robot_name: "",
        "robot_ws/branch": "",
        "robot_ws/status": "",
        "research_ws/remote": "",
        "research_ws/branch": "",
        "research_ws/status": "",
        "dashboard_ws/remote": "",
        "dashboard_ws/branch": "",
        "dashboard_ws/status": "",
        battery: "",
        last_online: "",
        ssid: "",
        ip: "",
        hostname: "",
        user: "",
        selected: false
    };
    node.warn(`New robot with uuid of [${serial}] by [${manufacturer}]`);
}

// Update the correct field based on topic
let b_prefix = "<span style='color: blue; font - weight: bold;'>"
let r_prefix = "<span style='color: green; font - weight: bold;'>"
let d_prefix = "<span style='color: red; font - weight: bold;'>"
let suffix = "</span>"

if (topicParts[3] === "dashboard") {
    if (topicParts[4] === "meta") {
        if (topicParts[5] === "robot_name") fleetData[manufacturer][serial].robot_name = value;
        if (topicParts[5] === "last_online") fleetData[manufacturer][serial].last_online = value;
        if (topicParts[5] === "battery") fleetData[manufacturer][serial].battery = value;
        if (topicParts[5] === "ssid") fleetData[manufacturer][serial].ssid = value;
        if (topicParts[5] === "ip") fleetData[manufacturer][serial].ip = value;
        if (topicParts[5] === "hostname") fleetData[manufacturer][serial].hostname = value;
        if (topicParts[5] === "user") fleetData[manufacturer][serial].user = value;
    } else if (topicParts[4] === "workspaces") {
        if (topicParts[5] === "robot_ws") {
            if (topicParts[6] === "git_branch") fleetData[manufacturer][serial]["robot_ws/branch"] = b_prefix + value + suffix;
            if (topicParts[6] === "git_status") fleetData[manufacturer][serial]["robot_ws/status"] = b_prefix + value + suffix;
        }
        if (topicParts[5] === "research_ws") {
            if (topicParts[6] === "git_branch") fleetData[manufacturer][serial]["research_ws/branch"] = r_prefix + value + suffix;
            if (topicParts[6] === "git_status") fleetData[manufacturer][serial]["research_ws/status"] = r_prefix + value + suffix;
            if (topicParts[6] === "git_remote") fleetData[manufacturer][serial]["research_ws/remote"] = r_prefix + value + suffix;
        }
        if (topicParts[5] === "dashboard_ws") {
            if (topicParts[6] === "git_branch") fleetData[manufacturer][serial]["dashboard_ws/branch"] = d_prefix + value + suffix;
            if (topicParts[6] === "git_status") fleetData[manufacturer][serial]["dashboard_ws/status"] = d_prefix + value + suffix;
            if (topicParts[6] === "git_remote") fleetData[manufacturer][serial]["dashboard_ws/remote"] = d_prefix + value + suffix;
        }
    }
}

// Store the updated data persistently
flow.set("fleetData", fleetData);

// Convert stored data to array format for ui_table
msg.payload = Object.values(fleetData).flatMap(manufacturerData => Object.values(manufacturerData));

return msg;

