// Extract form fields from msg.payload
let formData = msg.payload;  // Assumes payload contains {remote, branch, time, date, token}

// Retrieve fleetData from flow context
let fleetData = flow.get("fleetData") || {};

// Initialize an array to store MQTT messages
let mqttMessages = [];

// Iterate through fleetData to find selected items
for (const [manufacturer, serials] of Object.entries(fleetData)) {
    for (const [serial, data] of Object.entries(serials)) {
        // Check if 'selected' exists and is true
        if (data.selected === true) {
            // Construct MQTT topic
            for (const [key, value] of Object.entries(formData)) {
                let topic = `mrs_fleet_dashboard/${manufacturer}/${serial}/dashboard/updates/robot_ws/${key}`;

                // Construct MQTT message
                let mqttMessage = {
                    topic: topic,
                    payload: JSON.stringify(value),  // Sending the form data as JSON
                    qos: 1,
                    retain: false
                };

                // Add to messages list
                mqttMessages.push(mqttMessage);
            }
        }
    }
}

// Return messages for MQTT output node
return [mqttMessages];

